function SpawnFood (list: Image[], num: number) {
    if (Render.isViewMode(ViewMode.raycastingView)) {
        SpawnLoc = tiles.getTilesByType(sprites.castle.tilePath5)
        sprites.destroyAllSpritesOfKind(SpriteKind.Food)
        for (let index = 0; index < num; index++) {
            mySprite2 = sprites.create(list._pickRandom(), SpriteKind.Food)
            tiles.placeOnTile(mySprite2, SpawnLoc.removeAt(randint(0, SpawnLoc.length) - 1))
        }
    } else {
        sprites.destroyAllSpritesOfKind(SpriteKind.Food)
    }
}
